var searchUrl=global_settings.urlCORS+"/api/filtroApi/";registrationModule.factory("searchRepository",function(r){return{get:function(e){return r.get(searchUrl+"1|"+e)},getDivision:function(){return r.get(searchUrl+"1")},getEmpresa:function(e){return r.get(searchUrl+"2|"+e)},getSucursal:function(e,t){return r.get(searchUrl+"3|"+e+"|"+t)},getDepartamento:function(e,t,n){return r.get(searchUrl+"4|"+e+"|"+t+"|"+n)},getTipos:function(){return r.get(searchUrl+"6|0")},getProveedor:function(e){return r.get(searchUrl+"5|"+e)},getFolios:function(e){return r.get(searchUrl+"7|"+e)},update:function(e){return r.post(searchUrl+"2|"+e)}}});